    let count = 0;
    this.state = {
      time : 0
    };
    this.updateClock = this.updateClock.bind(this);

  }

componentDidMount(){
  let count = 0;
  console.log("yes");

  setInterval(function () {
    count = count + 1;
    this.updateClock(count);
