import React from 'react';
import ReactDOM from 'react-dom';
import logo from './logo.svg';
import './App.css';





class App extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
      hour : '',
      minute : '',
      second : '',
      title : ''

    }
    this.updateClock = this.updateClock.bind(this)

  }

componentDidMount(){





  var me = this;
  setInterval(function () {
    var d = new Date();
    me.updateClock(d)
  }, 1000);
}

updateClock(props) {
    console.log(props);
    this.setState({
      hour : props.getHours(),
      minute : props.getMinutes(),
      second : props.getSeconds()
    })

    if(this.state.second % 5){
      this.setState({
        title : 'Buzz'
      })
    }


}


  render() {
    return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {this.state.hour} {this.state.minute} {this.state.second}.</h2>
        <h3>{this.state.title}</h3>
      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);

export default App;
