import React from 'react';
import ReactDOM from 'react-dom';
import logo from './logo.svg';
import './App.css';





class App extends React.Component {
  constructor(props) {
    super(props);
      this.state = {
      hour : '',
      minute : '',
      second : '',
      title : ''

    }
    this.updateClock = this.updateClock.bind(this)

  }

componentDidMount(){
  var d = new Date();
  var h = d.getHours();
  var m = d.getMinutes();
  var s = d.getSeconds();
  count = s;
this.setState({
  hour : h,
  minute : m,
  second : s
})


  var me = this;
  setInterval(function () {
    count = s + 1;
    me.updateClock(count)
  }, 1000);
}

updateClock(props) {
    console.log(props);
    this.setState({
      second : props
    });



}


  render() {
    return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {this.state.hour} {this.state.minute} {this.state.second}.</h2>
      </div>
    );
  }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);

export default App;
